(322, 'ig_es_admin_new_sub_subject', 'Cinemalaya - New email subscription', 'yes'),
(323, 'ig_es_admin_new_sub_content', 'Hi Admin,\r\n\r\nCongratulations! You have a new subscriber.\r\n\r\nName : {{NAME}}\r\nEmail: {{EMAIL}}\r\nGroup: {{GROUP}}\r\n\r\nHave a nice day :)\r\nCinemalaya', 'yes'),
