(325, 'ig_es_welcomesubject', 'Cinemalaya - Welcome!', 'yes'),
(326, 'ig_es_welcomecontent', 'Hi {{NAME}},\r\n\r\nThank you for subscribing to Cinemalaya.\r\n\r\nWe are glad to have you onboard.\r\n\r\nBest,\r\nCinemalaya\r\n\r\nGot subscribed to Cinemalaya by mistake? Click <a href=\'{{LINK}}\'>here</a> to unsubscribe.', 'yes'),
(327, 'ig_es_optintype', 'Double Opt In', 'yes'),
(328, 'ig_es_confirmsubject', 'Cinemalaya - Please confirm your subscription', 'yes'),
(329, 'ig_es_confirmcontent', 'Hi {{NAME}},\r\n\r\nWe have received a subscription request from this email address. Please confirm it by <a href=\'{{LINK}}\'>clicking here</a>.\r\n\r\nIf you still cannot subscribe, please copy this link and paste it in your browser :\r\n{{LINK}} \r\n\r\nThank You\r\nCinemalaya', 'yes'),
(330, 'ig_es_optinlink', 'http://throughtheeyes.info/iteration1/?es=optin&db={{DBID}}&email={{EMAIL}}&guid={{GUID}}', 'yes'),
(331, 'ig_es_unsublink', 'http://throughtheeyes.info/iteration1/?es=unsubscribe&db={{DBID}}&email={{EMAIL}}&guid={{GUID}}', 'yes'),
(332, 'ig_es_unsubcontent', 'No longer interested in emails from Cinemalaya?. Please <a href=\'{{LINK}}\'>click here</a> to unsubscribe', 'yes'),
(333, 'ig_es_unsubtext', 'Thank You, You have been successfully unsubscribed. You will no longer hear from us.', 'yes'),
(334, 'ig_es_successmsg', 'You have been successfully subscribed.', 'yes'),
(335, 'ig_es_suberror', 'Oops.. Your request couldn\'t be completed. This email address seems to be already subscribed / blocked.', 'yes'),
(336, 'ig_es_unsuberror', 'Oops.. There was some technical error. Please try again later or contact us.', 'yes'),
