(346, 'ig_es_sentreport', 'Hi Admin,\n\nEmail has been sent successfully to {{COUNT}} email(s). Please find the details below:\n\nUnique ID: {{UNIQUE}}\nStart Time: {{STARTTIME}}\nEnd Time: {{ENDTIME}}\nFor more information, login to your dashboard and go to Reports menu in Email Subscribers.\n\nThank You.', 'yes'),
(347, 'ig_es_sentreport_subject', 'Your email has been sent', 'yes'),
(348, 'ig_es_cronurl', 'http://throughtheeyes.info/iteration1/?es=cron&guid=kwhypt-fhdrvk-bihwrd-jlsuba-vqjhcz', 'yes'),
